/*! MAIN.JS - v1.0.3 - 2013-03-02
 */
$(function(){



	$("a.info").click(function(e){
		e.preventDefault();

		//Hide
		$("p.navinfo").animate({"bottom": "-100px"},800, "easeInOutExpo");
		$("#portfolio").delay(400).animate({"bottom": "-150%"},1000, "easeInOutExpo");
		$(this).animate({"bottom": "-100px"},800, "easeInOutExpo");
		
		//Show
		$("#about").delay(400).animate({"top": "0%"},1000, "easeInOutExpo");
		$("a.close-info").delay(400).animate({"bottom": "60px"},1400, "easeInOutExpo");
	});

	$("a.close-info").click(function(e){
		e.preventDefault();

		//Hide
		$("#about").animate({"top": "-150%"},1000, "easeInOutExpo");
		$("a.close-info").animate({"bottom": "-100px"},800, "easeInOutExpo");

		//Show
		$("#portfolio").animate({"bottom": "0%"},1000, "easeInOutExpo");
		$("p.navinfo").animate({"bottom": "60px"},1400, "easeInOutExpo");
		$("a.info").animate({"bottom": "60px"},1400, "easeInOutExpo");

	});


});





/* Init load
================================================== */

$(window).load(function(){
	var check = 0;
	//Init slider

	if(!Modernizr.touch){
		$(".flexslider").flexslider({
			animation: "slide",
			controlNav: false,
			directionNav: false,
			slideshow: false,
			animationSpeed: 1000,
			useCSS: false,
			easing: "easeInOutExpo",
			start: function(slider){
				$('.flexslider').hammer().on("dragleft", function(event) {
			        slider.flexAnimate(slider.getTarget("next"));
			    });
			    $('.flexslider').hammer().on("dragright", function(event) {
			        slider.flexAnimate(slider.getTarget("prev"));
			    });
			 
			},
			after: function(){
				if(!check){$("p.navinfo").animate({"opacity": "0"}, 400);check=1;}
			}
		});
	}else{
		$(".flexslider").flexslider({
			animation: "slide",
			controlNav: false,
			directionNav: false,
			slideshow: false,
			animationSpeed: 600,
			after: function(){
				if(!check){$("p.navinfo").animate({"opacity": "0"}, 400);check=1;}
			}
		});
	}

	

	//Hide loader show content
	$("#loader").delay(1000).animate({"bottom": "-100px", "opacity": "0"},1000, "easeInOutExpo", function(){

		$("#portfolio").animate({"bottom": "0%", "opacity": "1"},1000, "easeInOutExpo");
		$("p.navinfo").delay(200).animate({"bottom": "60px"},1000, "easeInOutExpo");
		$("a.info").delay(200).animate({"bottom": "60px"},1000, "easeInOutExpo");
	});

});
 
 
